mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete
FROM chongwujiyang a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.yonghu_id = #{params.yonghuId} )
and a.chongwu_name like CONCAT('%',#{params.chongwuName},'%')
and a.chongwu_types = #{params.chongwuTypes}
and UNIX_TIMESTAMP(a.jiyang_riqi_time) >= UNIX_TIMESTAMP(#{params.jiyangRiqiTimeStart})
and UNIX_TIMESTAMP(a.jiyang_riqi_time) <= UNIX_TIMESTAMP(#{params.jiyangRiqiTimeEnd})
and a.jiyang_tianshu >= #{params.jiyangTianshuStart}
and a.jiyang_tianshu <= #{params.jiyangTianshuEnd}
and a.jiyang_tianshu = #{params.jiyangTianshu}
and a.jiyangdizhi like CONCAT('%',#{params.jiyangdizhi},'%')
and a.lianxiren_name like CONCAT('%',#{params.lianxirenName},'%')
and a.lianxiren_phone like CONCAT('%',#{params.lianxirenPhone},'%')
and a.chongwujiyang_content like CONCAT('%',#{params.chongwujiyangContent},'%')
and a.chongwujiyang_yesno_types = #{params.chongwujiyangYesnoTypes}
and a.chongwujiyang_yesno_text like CONCAT('%',#{params.chongwujiyangYesnoText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
