mapper.select:
SELECT
-- 级联表的字段 ,chongwulingyang.chongwulingyang_name as chongwulingyangName ,chongwulingyang.chongwu_types as chongwuTypes ,chongwulingyang.chongwulingyang_photo as chongwulingyangPhoto ,chongwulingyang.jieshu_types as jieshuTypes ,chongwulingyang.chongwulingyang_content as chongwulingyangContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete
FROM chongwulingyangshenhe a left JOIN chongwulingyang chongwulingyang ON a.chongwulingyang_id = chongwulingyang.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.chongwulingyang_id = #{params.chongwulingyangId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.chongwurenlingshenhe_text like CONCAT('%',#{params.chongwurenlingshenheText},'%')
and a.chongwulingyangshenhe_yesno_types = #{params.chongwulingyangshenheYesnoTypes}
and a.chongwulingyangshenhe_yesno_text like CONCAT('%',#{params.chongwulingyangshenheYesnoText},'%')
and a.chongwulingyang_id IS NOT NULL
and chongwulingyang.chongwulingyang_name like CONCAT('%',#{params.chongwulingyangName},'%')
and chongwulingyang.chongwu_types = #{params.chongwuTypes}
and chongwulingyang.jieshu_types = #{params.jieshuTypes}
and chongwulingyang.chongwulingyang_content like CONCAT('%',#{params.chongwulingyangContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
